import "../styles/discordActivity.css";
import { useEffect, useState } from "react";
import obsidianIcon from "../assets/obsidianIcon.png";
import { fetchLanyard } from "../hooks/useLanyard";
export function DiscordActivity() {
  const [data, setData] = useState<any | null>(null);

  useEffect(() => {
    fetchLanyard().then((json) => setData(json.data));
  }, []);

  if (!data) return <p>Cargando presencia...</p>;

  function resolveActivityImage(appId: string, key?: string | null) {
    if (!key) return null;
    if (key.startsWith("http")) return key; // ya es URL
    if (key.startsWith("mp:"))
      // media proxy
      return "https://media.discordapp.net/" + key.slice(3);
    // asset subido al “Application”
    return `https://cdn.discordapp.com/app-assets/${appId}/${key}.png`;
  }

  const dsStatus = data.discord_status;
  const dsSpotify = data.spotify; // no null check lol
  const dsActivites = data.activities;

  interface Activity {
    name: string;
    [key: string]: any;
  }

  interface SpotifyData {
    [key: string]: any;
  }

  interface LanyardData {
    discord_status: string;
    spotify: SpotifyData | null;
    data: {
      activities: Activity[];
    };
  }

  const code = data.activities.find(
    (a: any) => a.name === "Visual Studio Code"
  );
  const appId = code?.application_id;
  const largeImg = resolveActivityImage(appId, code?.assets?.large_image);
  const smallImg = resolveActivityImage(appId, code?.assets?.small_image);
  const repoUrl = code?.metadata?.button_urls?.[0] ?? null;
  const start = code?.timestamps?.start;
  const end = code?.timestamps?.end ?? Date.now();
  const durMs = end - start;
  const mins = Math.floor(durMs / 60000);
  const mathActivity = dsActivites.find(
    (dsActivites: Activity) => dsActivites.name === "obsidian"
  );

  const albumArt = dsSpotify.album_art_url;
  /* 
    Actividades de interes:
    Spotify - Code - Obsidian 
    NO pueden aparecer una atras de la otra, en todo caso se hace 
    un carousel (automatico?)
    segun el estado -> segun el color de background
  */

  console.log(dsActivites);
  console.log(dsSpotify);
  return (
    <div className={`ds-activity ${dsStatus} discord-container`}>
      <div className="activity">
        <a href={`https://open.spotify.com/track/${dsSpotify.track_id}`}>
          <img className="activity-image" src={albumArt} alt=""></img>
        </a>
        <h3>{dsSpotify.artist}</h3>
        <h4>{dsSpotify.song}</h4>
        <span>
          {dsSpotify.timestamps.start} / {dsSpotify.timestamps.end}
        </span>
      </div>
      <div>
        <div className="ds-activity code">
          <img className="activity-image" src={largeImg} alt={code?.details} />
          <h3>{code?.details}</h3> {/* p.ej. "Editing DiscordActivity.tsx" */}
          <h6>{code?.state}</h6> {/* p.ej. "Workspace: aboutMe" */}
          <h4>{code?.assets?.large_text}</h4> {/* p.ej. "Editing a TSX file" */}
          <time>
            {mins}
            {/* si hay end, mostrás duración */}
          </time>
        </div>
        {/* add where im active*/}
      </div>
    </div>
  );
}

export default DiscordActivity;
