import { CustomStatus } from "./CustomStatus";
import { formatMusicTime } from "../../utils/discordUtils";

type Maybe<T> = T | null | undefined;

// Define correctamente el tipo de datos de Spotify
interface SpotifyData {
  track_id?: string;
  song?: string;
  artist?: string;
  album_art_url?: string;
  timestamps?: {
    start?: number;
    end?: number;
  };
}

// Define las props que recibe el componente
interface SpotifyProps {
  spotify: SpotifyData; // Cambia de Maybe<string> a SpotifyData
  emojiUrl: Maybe<string>;
  statusText: Maybe<string>;
  albumArt: string;
  musicTime: {
    current: string;
    total: string;
    progress: number;
  };
}

export function Spotify({
  spotify,
  emojiUrl,
  statusText,
  albumArt,
  musicTime, // Recibe musicTime calculado desde el padre
}: SpotifyProps) {
  return (
    <>
      <div className="album-container">
        <a href={`https://open.spotify.com/track/${spotify?.track_id || ""}`}>
          <img className="activity-image" src={albumArt} alt=""></img>
        </a>
        <CustomStatus emojiUrl={emojiUrl} statusText={statusText} />
      </div>
      <div className="activity-container">
        <a href={`https://open.spotify.com/track/${spotify?.track_id}`}>
          <h3 className="activity-detail">{spotify?.song}</h3>
        </a>
        <h4 className="activity-state">{spotify?.artist}</h4>
        <span className="activity-time">
          {musicTime.current} / {musicTime.total}
        </span>
        <div className="progress-bar">
          <div className="progress-track">
            <div
              className="progress-fill"
              style={{ width: `${musicTime.progress}%` }}
            ></div>
          </div>
        </div>
      </div>
    </>
  );
}

export default Spotify;
