import "../styles/discordActivity.css";
import { useEffect, useState } from "react";
import { fetchLanyard } from "../hooks/useLanyard";
export function DiscordActivity() {
  /* 
    Actividades de interes:
    Spotify - Code - Obsidian 
    NO pueden aparecer una atras de la otra, en todo caso se hace 
    un carousel (automatico?)
    segun el estado -> segun el color de background
  */

  const [data, setData] = useState<any | null>(null);

  useEffect(() => {
    fetchLanyard().then((json) => setData(json.data));
  }, []);

  if (!data) return <p>Cargando presencia...</p>;

  let dsStatus = data.discord_status;
  let dsColor = document.querySelector(".ds-activity");

  if (dsColor) {
    switch (dsStatus) {
      case "online":
        dsColor.style.backgroundColor = "rgba(0, 255, 127, 0.25) ";
        break;
      case "idle":
        dsColor.style.backgroundColor = "rgba(255, 145, 0, 0.25)";
        break;
      case "dnd":
        dsColor.style.backgroundColor = "rgba(255, 42, 42, 0.25)";
        break;
      case "offline":
        dsColor.style.backgroundColor = "rgba(128, 128, 128, 0.25)";
        break;
    }
  }

  return (
    <div className="discord-container">
      <div className="ds-activity obsidian"></div>
    </div>
  );
}

export default DiscordActivity;
