import "../styles/rightColumn/countdown.css";

export function Countdown({ icon, count }: { icon: string; count: number }) {
  function upLastKey() {
    setTimeout(() => {
      const lastKey = document.querySelector(
        ".cd-numbers span:last-child"
      ) as HTMLElement;
      if (!(lastKey.innerText === "9")) {
        lastKey.innerText = String(Number(lastKey.innerText) + 1);
      } else {
        lastKey.innerText = "0";
        upLastKey();
      }
    }, 2);
  }
  while (true) {
    upLastKey();
  }
  return (
    <div className="countdown-container">
      {" "}
      <img className="cd-icon" src={icon} alt="icono countdown" />{" "}
      <div className="cd-numbers">
        {String(count)
          .split("")
          .map((digit, index) => (
            <span key={index} className="cd-numbers">
              {digit}
            </span>
          ))}
      </div>
    </div>
  );
}

export default Countdown;
